    public static int threeSumMulti(int[] arr, int target) {
        int[] count = new int[301];
        dp = new Integer[arr.length][target + 1];
        for (int i : arr) {
            count[i]++;
        }
        Arrays.sort(arr);
        int ans = 0;
        for (int i = 0; i < arr.length; i++) {
            count[arr[i]]--;
            ans += threeSumMultiProcess(arr, count, target - arr[i], i + 1);
            ans %= K;
        }
        return ans;
    }


    private static int threeSumMultiProcess(int[] arr, int[] count, int target, int index) {

        if (index >= arr.length || arr[index] > target) {
            return 0;
        }
        if (dp[index][target] != null) {
            return dp[index][target];
        }
        count[arr[index]]--;
        int c = target - arr[index];
        int ans = c >= 0 ? count[c] : 0;
        ans += (threeSumMultiProcess(arr, count, target, index + 1) % K);
        ans %= K;
        count[arr[index]]++;
        dp[index][target] = ans;
        return ans;
    }
